const items=document.querySelectorAll("img"),nbSlide=items.length,suivant=document.querySelector(".right"),precedent=document.querySelector(".left");let count=0;function slideSuivante(){items[count].classList.remove("active"),count<nbSlide-1?count++:count=0,items[count].classList.add("active")}function slidePrecedente(){items[count].classList.remove("active"),count>0?count--:count=nbSlide-1,items[count].classList.add("active")}function keyPress(e){37===e.keyCode?slidePrecedente():39===e.keyCode&&slideSuivante()}suivant.addEventListener("click",slideSuivante),precedent.addEventListener("click",slidePrecedente),document.addEventListener("keydown",keyPress);